import { Component } from "@angular/core";

@Component({
  selector: "app-root",
  templateUrl: "./app.component.html",
  styleUrls: ["./app.component.css"],
})
export class AppComponent {
  count: number = 0;

  incrementCount() {
    this.count++;
  }

  decrementCount() {
    this.count--;
  }
  inputValue(data : number) {
    this.count = data;
  }
  resetCount() {
    this.count = 0;
  }
}

