@Component({
  selector: "app-input",
  templateUrl: "./input.component.html",
  styleUrls: ["./input.component.css"],
})
export class InputComponent {
  @Input() chiCount: number;
  inputVal: number;

  ng
  changeValue() {
    console.log(this.inputVal);
  }
}
<div>
  <input
    [(ngModel)]="inputVal"
    (change)="changeValue()"
