<h3>Count : {{ count }}</h3>

<app-increment
  [chiCount]="count"
  (incrementEmiit)="incrementCount()"
></app-increment>
<app-decrement
  [chiCount]="count"
  (decrementEmit)="decrementCount()"
></app-decrement>
<app-reset [chiCount]="count" (resetEmit)="resetCount()"></app-reset>
<app-input></app-input>
